import colorama

'''
        colors:
    BLACK           = 30
    RED             = 31
    GREEN           = 32
    YELLOW          = 33
    BLUE            = 34
    MAGENTA         = 35
    CYAN            = 36
    WHITE           = 37
    RESET           = 39

    LIGHTBLACK_EX   = 90
    LIGHTRED_EX     = 91
    LIGHTGREEN_EX   = 92
    LIGHTYELLOW_EX  = 93
    LIGHTBLUE_EX    = 94
    LIGHTMAGENTA_EX = 95
    LIGHTCYAN_EX    = 96
    LIGHTWHITE_EX   = 97
'''

def lightred(text):
    return colorama.Fore.LIGHTRED_EX + text + colorama.Fore.RESET

def lightgreen(text):
    return colorama.Fore.LIGHTGREEN_EX + text + colorama.Fore.RESET
